use std::collections::{HashMap, HashSet};

/// Errors that can occur during encoding
#[derive(Debug, thiserror::Error)]
pub enum EncodingError {
    #[error("Unknown format")]
    UnknownFormat,
    #[error("Invalid message: {0}")]
    InvalidMessage(String),
    #[error("Untrusted context not supported")]
    UntrustedContextNotSupported,
}

/// Types of formatting tokens
#[derive(Clone, Debug, PartialEq, Eq, Hash, Serialize, Deserialize)]
pub enum FormattingToken {
    BeginMessageUser,
    EndMessageUser,
    BeginMessageAssistant,
    EndMessageAssistant,
    BeginMessageTool,
    EndMessageTool,
    BeginMessageAssistantToTool,
    EndMessageAssistantToTool,
    EndMessageDoneSampling,
    Channel,
    BeginUntrusted,
    EndUntrusted,
    MetaSep,
    MetaEnd,
}

/// Encoding configuration for a specific model or format
#[derive(Clone, Debug, Serialize, Deserialize)]
pub struct HarmonyEncoding {
    pub name: String,
    pub special_tokens: HashMap<FormattingToken, String>,
    pub stop_formatting_tokens: HashSet<FormattingToken>,
    pub tokenizer: Option<Box<dyn TokenizerTrait>>,
}

/// Trait for tokenizers
pub trait TokenizerTrait: Send + Sync {
    fn encode(&self, text: &str) -> Vec<u32>;
    fn decode(&self, tokens: &[u32]) -> String;
}

impl FormattingToken {
    /// Convert the formatting token to its string representation
    pub fn as_str(&self) -> &'static str {
        match self {
            FormattingToken::BeginMessageUser => "<|message_user|>",
            FormattingToken::EndMessageUser => "<|end_message_user|>",
            FormattingToken::BeginMessageAssistant => "<|message_assistant|>",
            FormattingToken::EndMessageAssistant => "<|end_message_assistant|>",
            FormattingToken::BeginMessageTool => "<|message_tool|>",
            FormattingToken::EndMessageTool => "<|end_message_tool|>",
            FormattingToken::BeginMessageAssistantToTool => "<|call|>",
            FormattingToken::EndMessageAssistantToTool => "<|call|>",
            FormattingToken::EndMessageDoneSampling => "<|done|>",
            FormattingToken::Channel => "<|channel|>",
            FormattingToken::BeginUntrusted => "<|untrusted|>",
            FormattingToken::EndUntrusted => "<|end_untrusted|>",
            FormattingToken::MetaSep => "<|meta_sep|>",
            FormattingToken::MetaEnd => "<|meta_end|>",
        }
    }
}

impl HarmonyEncoding {
    /// Create a new encoding configuration
    pub fn new(name: String) -> Self {
        Self {
            name,
            special_tokens: HashMap::new(),
            stop_formatting_tokens: HashSet::new(),
            tokenizer: None,
        }
    }

    /// Encode text using this configuration
    pub fn encode(&self, text: &str) -> Result<Vec<u32>, EncodingError> {
        if let Some(tokenizer) = &self.tokenizer {
            Ok(tokenizer.encode(text))
        } else {
            Err(EncodingError::UnknownFormat)
        }
    }

    /// Decode tokens using this configuration
    pub fn decode(&self, tokens: &[u32]) -> Result<String, EncodingError> {
        if let Some(tokenizer) = &self.tokenizer {
            Ok(tokenizer.decode(tokens))
        } else {
            Err(EncodingError::UnknownFormat)
        }
    }

    /// Get the string representation of a formatting token
    pub fn format_token(&self, token: &FormattingToken) -> Option<&String> {
        self.special_tokens.get(token)
    }

    /// Check if a token is a stop token
    pub fn is_stop_token(&self, token: &FormattingToken) -> bool {
        self.stop_formatting_tokens.contains(token)
    }
}

