{"branch": "issue-management-workflow", "content": "name: Issue Management Automation

on:
  issues:
    types: [opened, labeled]

jobs:
  issue-triage:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened'
    outputs:
      is-epic: ${{ steps.check-epic.outputs.is-epic }}
      priority-level: ${{ steps.assign-priority.outputs.priority-level }}
    steps:
      - name: Check if issue is Epic
        id: check-epic
        run: |
          title="${{ github.event.issue.title }}"
          title_lower=$(echo "$title" | tr '[:upper:]' '[:lower:]')
          if [[ "$title_lower" == *"epic"* ]]; then
            echo "is-epic=true" >> $GITHUB_OUTPUT
          else
            echo "is-epic=false" >> $GITHUB_OUTPUT
          fi

      - name: Auto-assign category labels
        uses: actions/github-script@v7
        with:
          script: |
            const title = context.payload.issue.title.toLowerCase();
            const labels = [];

            // Category labels based on issue title
            if (title.includes('bug')) {
              labels.push('bug');
            }
            if (title.includes('epic')) {
              labels.push('epic');
            }
            if (title.includes('maintenance')) {
              labels.push('maintenance');
            }

            // Create labels if they don't exist
            const existingLabels = await github.rest.issues.listLabelsForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo
            });

            const existingLabelNames = existingLabels.data.map(label => label.name);

            const allLabels = [
              { name: 'bug', color: 'd73a4a', description: 'Something isn\'t working' },
              { name: 'enhancement', color: 'a2eeef', description: 'New feature or request' },
              { name: 'epic', color: '7057ff', description: 'Large feature requiring multiple sub-tasks' },
              { name: 'maintenance', color: 'fbca04', description: 'Maintenance and housekeeping tasks' },
              { name: 'priority-critical', color: 'b60205', description: 'Critical priority issue' },
              { name: 'priority-high', color: 'd93f0b', description: 'High priority issue' },
              { name: 'priority-medium', color: 'fbca04', description: 'Medium priority issue' },
              { name: 'priority-low', color: '0e8a16', description: 'Low priority issue' },
              { name: 'needs-triage', color: 'e4e669', description: 'Needs to be reviewed by maintainers' },
              { name: 'needs-review', color: 'fef2c0', description: 'Awaiting review from maintainers' },
              { name: 'first-time-contributor', color: '7057ff', description: 'Issue created by first-time contributor' }
            ];

            for (const label of allLabels) {
              if (!existingLabelNames.includes(label.name)) {
                try {
                  await github.rest.issues.createLabel({
                    owner: context.repo.owner,
                    repo: context.repo.repo,
                    name: label.name,
                    color: label.color,
                    description: label.description
                  });
                } catch (error) {
                  console.log(`Label ${label.name} might already exist: ${error.message}`);
                }
              }
            }

            // Add category labels to issue
            if (labels.length > 0) {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: context.payload.issue.number,
                labels: labels
              });
            }

      - name: Auto-assign priority labels
        id: assign-priority
        uses: actions/github-script@v7
        with:
          script: |
            const title = context.payload.issue.title.toLowerCase();
            const body = (context.payload.issue.body || '').toLowerCase();
            const content = title + ' ' + body;

            let priorityLabel = 'priority-medium'; // default

            // Check for priority keywords (highest priority wins)
            if (content.includes('critical') || content.includes('urgent') || 
                content.includes('production') || content.includes('outage')) {
              priorityLabel = 'priority-critical';
            } else if (content.includes('important') || content.includes('high') || 
                       content.includes('blocking')) {
              priorityLabel = 'priority-high';
            } else if (content.includes('medium') || content.includes('normal')) {
              priorityLabel = 'priority-medium';
            } else if (content.includes('low') || content.includes('nice-to-have') || 
                       content.includes('minor')) {
              priorityLabel = 'priority-low';
            }

            // Add priority and needs-triage labels
            await github.rest.issues.addLabels({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: context.payload.issue.number,
              labels: [priorityLabel, 'needs-triage']
            });

            core.setOutput('priority-level', priorityLabel);

  task-breakdown:
    runs-on: ubuntu-latest
    needs: issue-triage
    if: needs.issue-triage.outputs.is-epic == 'true'
    steps:
      - name: Create sub-issues for Epic
        uses: actions/github-script@v7
        with:
          script: |
            const originalTitle = context.payload.issue.title;
            const parentNumber = context.payload.issue.number;

            const tasks = [
              'Requirements Analysis',
              'Design and Architecture', 
              'Implementation',
              'Testing and Documentation'
            ];

            const subIssueNumbers = [];

            // Create sub-issues
            for (let i = 0; i < tasks.length; i++) {
              const taskTitle = `[SUBTASK] ${originalTitle} - Task ${i + 1}: ${tasks[i]}`;
              const taskBody = `Related to #${parentNumber}\n\nThis is a sub-task of the epic: ${originalTitle}\n\n## Task Description\n${tasks[i]} for the epic feature.\n\n## Acceptance Criteria\n- [ ] Task completed\n- [ ] Code reviewed\n- [ ] Tests written`;

              const subIssue = await github.rest.issues.create({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: taskTitle,
                body: taskBody,
                labels: ['enhancement', 'needs-review']
              });

              subIssueNumbers.push(subIssue.data.number);
            }

            // Update parent issue with checklist
            const epicTasksSection = `\n\n## Epic Tasks\n${subIssueNumbers.map((num, i) => `- [ ] Task ${i + 1}: ${tasks[i]} (#${num})`).join('\n')}`;
            const updatedBody = (context.payload.issue.body || '') + epicTasksSection;

            await github.rest.issues.update({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: parentNumber,
              body: updatedBody
            });

