              owner: context.repo.owner,
              repo: context.repo.repo,
              creator: author,
              state: 'all',
              per_page: 100
            });

            const userIssues = issues.data.filter(issue => issue.user.login === author);
            const isFirstTimeContributor = userIssues.length === 1; // Only the current issue

            let welcomeMessage = '';
            if (isFirstTimeContributor) {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: context.payload.issue.number,
                labels: ['first-time-contributor']
              });

              welcomeMessage = `üëã Welcome @${author}! Thank you for creating your first issue in this repository. We appreciate your contribution to the project.\n\n`;
            }

            // Get issue labels to determine response type
            const issue = await github.rest.issues.get({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: context.payload.issue.number
            });

            const labels = issue.data.labels.map(label => label.name);
            let responseMessage = welcomeMessage;

            if (labels.includes('bug')) {
              responseMessage += `## Bug Report Guidelines\n\nThank you for reporting this bug! To help us resolve this issue quickly, please ensure you've provided:\n\n- [ ] Clear steps to reproduce the issue\n- [ ] Expected vs actual behavior\n- [ ] Environment details (OS, browser, Node.js version)\n- [ ] Any relevant error messages or screenshots\n\nOur team will review this bug report and prioritize it accordingly.`;
            } else if (labels.includes('epic')) {
              responseMessage += `## Feature Request Process\n\nThank you for proposing this epic feature! Epic features are significant additions that require careful planning and implementation.\n\n**Next Steps:**\n- [ ] Requirements analysis and scope definition\n- [ ] Technical design and architecture review\n- [ ] Breaking down into smaller implementation tasks\n- [ ] Timeline and resource planning\n\nSub-tasks have been automatically created for this epic. We'll begin with requirements analysis and keep you updated on progress.`;
            } else if (labels.includes('maintenance')) {
              responseMessage += `## Maintenance Guidelines\n\nThank you for identifying this maintenance task! Maintenance activities are crucial for keeping our codebase healthy and performant.\n\n**Evaluation Criteria:**\n- [ ] Impact assessment on existing functionality\n- [ ] Risk level and rollback strategy\n- [ ] Testing requirements\n- [ ] Resource allocation and timeline\n\nOur team will review this maintenance request and schedule it appropriately based on priority and available resources.`;
            } else {
              responseMessage += `## Issue Received\n\nThank you for creating this issue! Our team will review it and provide feedback soon.\n\nPlease ensure you've provided all necessary information to help us understand and address your request effectively.`;
            }

            // Post the response comment
            await github.rest.issues.createComment({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: context.payload.issue.number,
              body: responseMessage
            });

      - name: Set milestone for high priority issues
        uses: actions/github-script@v7
        with:
          script: |
            const priorityLevel = '${{ needs.issue-triage.outputs.priority-level }}';

            if (priorityLevel === 'priority-high' || priorityLevel === 'priority-critical') {
              // Check if milestone exists, create if not
              let milestoneNumber;
              try {
                const milestones = await github.rest.issues.listMilestones({
                  owner: context.repo.owner,
                  repo: context.repo.repo
                });

                const existingMilestone = milestones.data.find(m => m.title === 'v1.0.0');
                if (existingMilestone) {
                  milestoneNumber = existingMilestone.number;
                } else {
                  const newMilestone = await github.rest.issues.createMilestone({
                    owner: context.repo.owner,
                    repo: context.repo.repo,
                    title: 'v1.0.0',
                    description: 'Version 1.0.0 release milestone'
                  });
                  milestoneNumber = newMilestone.data.number;
                }

                // Assign milestone to issue
                await github.rest.issues.update({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: context.payload.issue.number,
                  milestone: milestoneNumber
                });
              } catch (error) {
                console.log(`Error setting milestone: ${error.message}`);
              }
            }

