arguments:
{"branch": "issue-management-workflow", "content": "name: Issue Management Automation

on:
  issues:
    types: [opened, labeled]

jobs:
  issue-triage:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened'
    outputs:
      issue-number: ${{ github.event.issue.number }}
      is-epic: ${{ steps.check-epic.outputs.is-epic }}
      priority-level: ${{ steps.set-priority.outputs.priority }}
      category-type: ${{ steps.set-category.outputs.category }}
    steps:
      - name: Check if Epic
        id: check-epic
        run: |
          title="${{ github.event.issue.title }}"
          if [[ "${title,,}" == *"epic"* ]]; then
            echo "is-epic=true" >> $GITHUB_OUTPUT
          else
            echo "is-epic=false" >> $GITHUB_OUTPUT
          fi

      - name: Set Category Labels
        id: set-category
        uses: actions/github-script@v7
        with:
          script: |
            const title = context.payload.issue.title.toLowerCase();
            let categoryLabel = '';

            if (title.includes('bug')) {
              categoryLabel = 'bug';
            } else if (title.includes('epic')) {
              categoryLabel = 'epic';
            } else if (title.includes('maintenance')) {
              categoryLabel = 'maintenance';
            }

            if (categoryLabel) {
              try {
                await github.rest.issues.addLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: context.issue.number,
                  labels: [categoryLabel]
                });
                console.log(`Added ${categoryLabel} label`);
                core.setOutput('category', categoryLabel);
              } catch (error) {
                console.log(`Label ${categoryLabel} might not exist, will be created automatically`);
                core.setOutput('category', categoryLabel);
              }
            }

      - name: Set Priority Labels
        id: set-priority
        uses: actions/github-script@v7
        with:
          script: |
            const title = context.payload.issue.title.toLowerCase();
            const body = (context.payload.issue.body || '').toLowerCase();
            const content = title + ' ' + body;

            let priorityLabel = 'priority-medium'; // default

            // Check for priority keywords (highest priority wins)
            if (content.includes('critical') || content.includes('urgent') || content.includes('production') || content.includes('outage')) {
              priorityLabel = 'priority-critical';
            } else if (content.includes('important') || content.includes('high') || content.includes('blocking')) {
              priorityLabel = 'priority-high';
            } else if (content.includes('low') || content.includes('nice-to-have') || content.includes('minor')) {
              priorityLabel = 'priority-low';
            } else if (content.includes('medium') || content.includes('normal')) {
              priorityLabel = 'priority-medium';
            }

            try {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: context.issue.number,
                labels: [priorityLabel]
              });
              console.log(`Added ${priorityLabel} label`);
              core.setOutput('priority', priorityLabel);
            } catch (error) {
              console.log(`Label ${priorityLabel} might not exist, will be created automatically`);
              core.setOutput('priority', priorityLabel);
            }

      - name: Add Initial Triage Label
        uses: actions/github-script@v7
        with:
          script: |
            try {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: context.issue.number,
                labels: ['needs-triage']
              });
              console.log('Added needs-triage label');
            } catch (error) {
              console.log('needs-triage label might not exist, will be created automatically');
            }

  task-breakdown:
    runs-on: ubuntu-latest
    needs: issue-triage
    if: needs.issue-triage.outputs.is-epic == 'true'
    steps:
      - name: Create Sub-Issues for Epic
        uses: actions/github-script@v7
        with:
          script: |
            const originalTitle = context.payload.issue.title;
            const parentNumber = context.issue.number;

            const tasks = [
              'Requirements Analysis',
              'Design and Architecture', 
              'Implementation',
              'Testing and Documentation'
            ];

            const subIssues = [];

            for (let i = 0; i < tasks.length; i++) {
              const taskNumber = i + 1;
              const taskName = tasks[i];
              const subIssueTitle = `[SUBTASK] ${originalTitle} - Task ${taskNumber}: ${taskName}`;
              const subIssueBody = `Related to #${parentNumber}\n\nThis is a subtask for the epic: ${originalTitle}\n\n**Task**: ${taskName}\n\n## Description\nPlease provide detailed description for this subtask.\n\n## Acceptance Criteria\n- [ ] Task requirements defined\n- [ ] Implementation completed\n- [ ] Testing completed\n- [ ] Documentation updated`;

              try {
                const subIssue = await github.rest.issues.create({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  title: subIssueTitle,
                  body: subIssueBody,
                  labels: ['enhancement', 'needs-review']
                });

                subIssues.push({
                  number: subIssue.data.number,
                  title: subIssueTitle
                });

                console.log(`Created sub-issue #${subIssue.data.number}: ${subIssueTitle}`);
              } catch (error) {
                console.error(`Failed to create sub-issue: ${error.message}`);
              }
            }

            // Update parent issue with Epic Tasks checklist
            if (subIssues.length > 0) {
              const currentBody = context.payload.issue.body || '';
              let checklistText = '\n\n## Epic Tasks\n';

              subIssues.forEach(subIssue => {
                checklistText += `- [ ] #${subIssue.number} - ${subIssue.title}\n`;
              });

              const updatedBody = currentBody + checklistText;

              try {
                await github.rest.issues.update({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: parentNumber,
                  body: updatedBody
                });
                console.log(`Updated parent issue #${parentNumber} with Epic Tasks checklist`);
              } catch (error) {
                console.error(`Failed to update parent issue: ${error.message}`);
              }
            }

  auto-response:
    runs-on: ubuntu-latest
    needs: issue-triage
    if: github.event.action == 'opened'
    steps:
      - name: Check First Time Contributor
        id: check-contributor
        uses: actions/github-script@v7
        with:
          script: |
            const author = context.payload.issue.user.login;

            try {
              // Get all issues created by this user in this repository
              const issues = await github.rest.issues.listForRepo({
                owner: context.repo.owner,
                repo: context.repo.repo,
                creator: author,
                state: 'all',
                per_page: 100
              });

              // Check if this is their first issue in this repo
              const isFirstIssue = issues.data.length === 1;

              if (isFirstIssue) {
                await github.rest.issues.addLabels({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: context.issue.number,
                  labels: ['first-time-contributor']
                });
                console.log('Added first-time-contributor label');
                core.setOutput('is-first-time', 'true');
              } else {
                core.setOutput('is-first-time', 'false');
              }
            } catch (error) {
              console.error('Error checking contributor status:', error);
              core.setOutput('is-first-time', 'false');
            }

      - name: Post Appropriate Response
        uses: actions/github-script@v7
        with:
          script: |
            const title = context.payload.issue.title.toLowerCase();
            const isFirstTime = '${{ steps.check-contributor.outputs.is-first-time }}' === 'true';

            let responseMessage = '';

            // Welcome message for first-time contributors
            if (isFirstTime) {
              responseMessage += 'üéâ **Welcome to the project!** Thank you for creating your first issue in this repository.\n\n';
            }

            // Type-specific responses
            if (title.includes('bug')) {
              responseMessage += 'üêõ **Bug Report Guidelines**\n\nThank you for reporting this bug! To help us resolve it quickly:\n\n';
              responseMessage += '- Please ensure you\'ve provided all the information requested in the bug report template\n';
              responseMessage += '- Include steps to reproduce the issue\n';
              responseMessage += '- Add screenshots or error messages if available\n';
              responseMessage += '- Specify your environment details\n\n';
              responseMessage += 'Our team will review this bug report and provide updates soon.';
            } else if (title.includes('epic')) {
              responseMessage += 'üöÄ **Feature Request Process**\n\nThank you for this epic feature request! Here\'s what happens next:\n\n';
              responseMessage += '- Sub-tasks have been automatically created for this epic\n';
              responseMessage += '- Each sub-task will be tracked individually\n';
              responseMessage += '- Please review the generated sub-tasks and add any missing details\n';
              responseMessage += '- The team will prioritize and assign these tasks accordingly\n\n';
              responseMessage += 'We appreciate your contribution to planning this feature!';
            } else if (title.includes('maintenance')) {
              responseMessage += 'üîß **Maintenance Guidelines**\n\nThank you for this maintenance request! Here\'s how we handle maintenance tasks:\n\n';
              responseMessage += '- Maintenance tasks are reviewed regularly by the team\n';
              responseMessage += '- Please provide clear justification for the maintenance priority\n';
              responseMessage += '- Include any potential risks or impacts\n';
              responseMessage += '- Consider backward compatibility requirements\n\n';
              responseMessage += 'This maintenance task will be scheduled based on its priority and impact.';
            } else {
              // Generic response for other issue types
              responseMessage += 'üìù **Issue Received**\n\nThank you for creating this issue! Our team will review it and provide feedback soon.\n\n';
              responseMessage += 'Please ensure you\'ve provided all necessary details to help us understand and address your request.';
            }

            // Post the comment
            await github.rest.issues.createComment({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: context.issue.number,
              body: responseMessage
            });

      - name: Set Milestone for High Priority Issues
        uses: actions/github-script@v7
        with:
          script: |
            const priority = '${{ needs.issue-triage.outputs.priority-level }}';

            if (priority === 'priority-high' || priority === 'priority-critical') {
              try {
                // First, try to get existing milestone
                let milestone = null;
                try {
                  const milestones = await github.rest.issues.listMilestones({
                    owner: context.repo.owner,
                    repo: context.repo.repo
                  });

                  milestone = milestones.data.find(m => m.title === 'v1.0.0');
                } catch (error) {
                  console.log('Error fetching milestones:', error);
                }

                // Create milestone if it doesn't exist
                if (!milestone) {
                  try {
                    const newMilestone = await github.rest.issues.createMilestone({
                      owner: context.repo.owner,
                      repo: context.repo.repo,
                      title: 'v1.0.0',
                      description: 'Release version 1.0.0'
                    });
                    milestone = newMilestone.data;
                    console.log('Created new milestone: v1.0.0');
                  } catch (error) {
                    console.log('Failed to create milestone:', error);
                    return;
                  }
                }

                // Assign milestone to issue
                if (milestone) {
                  await github.rest.issues.update({
                    owner: context.repo.owner,
                    repo: context.repo.repo,
                    issue_number: context.issue.number,
                    milestone: milestone.number
                  });
                  console.log(`Assigned milestone v1.0.0 to issue #${context.issue.number}`);
                }
              } catch (error) {
                console.error('Error setting milestone:', error);
              }
            }

      - name: Update Status from Triage to Review
        uses: actions/github-script@v7
        with:
          script: |
            try {
              // Remove needs-triage label
              await github.rest.issues.removeLabel({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: context.issue.number,
                name: 'needs-triage'
              });

              // Add needs-review label
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: context.issue.number,
                labels: ['needs-review']
              });

              console.log('Updated status from needs-triage to needs-review');
            } catch (error) {
              console.error('Error updating status labels:', error);
            }", "message": "Add issue management automation workflow", "owner": "mcpmark-eval", "path": ".github/workflows/issue-automation.yml", "repo": "mcpmark-cicd"}
