    outputs:
      issue-type: ${{ steps.categorize.outputs.issue-type }}
      priority-level: ${{ steps.prioritize.outputs.priority-level }}
    steps:
      - name: Categorize Issue
        id: categorize
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const title = issue.title.toLowerCase();
            const body = (issue.body || '').toLowerCase();

            let categoryLabel = '';
            let issueType = '';

            // Check title for category keywords
            if (title.includes('bug')) {
              categoryLabel = 'bug';
              issueType = 'bug';
            } else if (title.includes('epic')) {
              categoryLabel = 'epic';
              issueType = 'epic';
            } else if (title.includes('maintenance')) {
              categoryLabel = 'maintenance';
              issueType = 'maintenance';
            }

            // Add category label if found
            if (categoryLabel) {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                labels: [categoryLabel]
              });
            }

            core.setOutput('issue-type', issueType);

      - name: Prioritize Issue
        id: prioritize
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const title = issue.title.toLowerCase();
            const body = (issue.body || '').toLowerCase();
            const content = title + ' ' + body;

            let priorityLabel = 'priority-medium'; // default
            let priorityLevel = 'medium';

            // Check for priority keywords (highest priority wins)
            if (content.includes('critical') || content.includes('urgent') || 
                content.includes('production') || content.includes('outage')) {
              priorityLabel = 'priority-critical';
              priorityLevel = 'critical';
            } else if (content.includes('important') || content.includes('high') || 
                       content.includes('blocking')) {
              priorityLabel = 'priority-high';
              priorityLevel = 'high';
            } else if (content.includes('low') || content.includes('nice-to-have') || 
                       content.includes('minor')) {
              priorityLabel = 'priority-low';
              priorityLevel = 'low';
            } else if (content.includes('medium') || content.includes('normal')) {
              priorityLabel = 'priority-medium';
              priorityLevel = 'medium';
            }

            // Add priority and needs-triage labels
            await github.rest.issues.addLabels({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issue.number,
              labels: [priorityLabel, 'needs-triage']
            });

            core.setOutput('priority-level', priorityLevel);

  task-breakdown:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened'
    needs: issue-triage
    steps:
      - name: Create Epic Sub-issues
        if: needs.issue-triage.outputs.issue-type == 'epic'
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const originalTitle = issue.title;
            const parentNumber = issue.number;

            const taskNames = [
              'Requirements Analysis',
              'Design and Architecture', 
              'Implementation',
              'Testing and Documentation'
            ];

            const subIssues = [];

            // Create 4 sub-issues
            for (let i = 0; i < taskNames.length; i++) {
              const taskNumber = i + 1;
              const taskName = taskNames[i];
              const subIssueTitle = `[SUBTASK] ${originalTitle} - Task ${taskNumber}: ${taskName}`;
              const subIssueBody = `Related to #${parentNumber}

## Task Description
This is subtask ${taskNumber} of ${taskNames.length} for the epic: "${originalTitle}"

## Task: ${taskName}
Please complete the ${taskName.toLowerCase()} phase for this epic.

## Acceptance Criteria
- [ ] Task requirements defined
- [ ] Implementation completed
- [ ] Testing completed
- [ ] Documentation updated`;

              const subIssue = await github.rest.issues.create({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: subIssueTitle,
                body: subIssueBody,
                labels: ['enhancement', 'needs-review']
              });

              subIssues.push({
                number: subIssue.data.number,
                title: subIssueTitle
              });
            }

            // Update parent issue with checklist
            const checklistItems = subIssues.map(sub => 
              `- [ ] #${sub.number} - ${sub.title}`
            ).join('\n');

            const updatedBody = issue.body + `

## Epic Tasks
${checklistItems}`;

            await github.rest.issues.update({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: parentNumber,
              body: updatedBody
            });

