arguments:
{"branch": "pr-automation-workflow", "content": "name: Pull Request Automation

on:
  pull_request:
    types: [opened, synchronize, reopened]

jobs:
  code-quality:
    name: code-quality
    runs-on: ubuntu-latest
    steps:
      - name: Checkout code
        uses: actions/checkout@v4

      - name: Setup Node.js
        uses: actions/setup-node@v4
        with:
          node-version: '18'
          cache: 'npm'

      - name: Install dependencies
        run: npm ci

      - name: Run ESLint
        id: eslint
        run: |
          npm run lint > eslint-results.txt 2>&1 || echo "eslint_failed=true" >> $GITHUB_OUTPUT
          cat eslint-results.txt

      - name: Run Prettier check
        id: prettier
        run: |
          npx prettier --check . > prettier-results.txt 2>&1 || echo "prettier_failed=true" >> $GITHUB_OUTPUT
          cat prettier-results.txt

      - name: Post Code Quality Report
        uses: actions/github-script@v7
        if: always()
        with:
          script: |
            const fs = require('fs');

            let eslintResults = '';
            let prettierResults = '';

            try {
              eslintResults = fs.readFileSync('eslint-results.txt', 'utf8');
            } catch (e) {
              eslintResults = 'ESLint check could not be completed';
            }

            try {
              prettierResults = fs.readFileSync('prettier-results.txt', 'utf8');
            } catch (e) {
              prettierResults = 'Prettier check could not be completed';
            }

            const eslintFailed = '${{ steps.eslint.outputs.eslint_failed }}' === 'true';
            const prettierFailed = '${{ steps.prettier.outputs.prettier_failed }}' === 'true';

            const statusEmoji = (!eslintFailed && !prettierFailed) ? '✅' : '❌';

            const comment = `## ${statusEmoji} Code Quality Report

            ### ESLint Results
            ${eslintFailed ? '❌ ESLint found issues:' : '✅ ESLint passed successfully'}
            \`\`\`
            ${eslintResults}
            \`\`\`

            ### Prettier Results
            ${prettierFailed ? '❌ Prettier formatting issues found:' : '✅ Prettier formatting is correct'}
            \`\`\`
            ${prettierResults}
            \`\`\`

            ---
            *Code Quality Report generated by PR Automation Workflow*`;

            github.rest.issues.createComment({
              issue_number: context.issue.number,
              owner: context.repo.owner,
              repo: context.repo.repo,
              body: comment
            });

  testing-suite:
    name: testing-suite
    runs-on: ubuntu-latest
    steps:
      - name: Checkout code
        uses: actions/checkout@v4

      - name: Setup Node.js
        uses: actions/setup-node@v4
        with:
          node-version: '18'
          cache: 'npm'

      - name: Install dependencies
        run: npm ci

      - name: Run test suite with coverage
        id: tests
        run: |
          npm test -- --coverage > test-results.txt 2>&1 || echo "tests_failed=true" >> $GITHUB_OUTPUT
          cat test-results.txt

      - name: Upload coverage artifacts
        uses: actions/upload-artifact@v4
        if: always()
        with:
          name: coverage-report
          path: |
            coverage/
            test-results.txt
          retention-days: 30

      - name: Extract coverage summary
        id: coverage
        run: |
          if [ -f "coverage/lcov-report/index.html" ]; then
            # Try to extract coverage percentage from coverage files
            COVERAGE=$(grep -o '[0-9]*\.[0-9]*%' coverage/lcov-report/index.html | head -1 || echo "Coverage data not available")
            echo "coverage_percentage=$COVERAGE" >> $GITHUB_OUTPUT
          else
            echo "coverage_percentage=Coverage data not available" >> $GITHUB_OUTPUT
          fi

      - name: Post Test Coverage Report
        uses: actions/github-script@v7
        if: always()
        with:
          script: |
            const fs = require('fs');

            let testResults = '';
            try {
              testResults = fs.readFileSync('test-results.txt', 'utf8');
            } catch (e) {
              testResults = 'Test results could not be read';
            }

            const testsFailed = '${{ steps.tests.outputs.tests_failed }}' === 'true';
            const coverage = '${{ steps.coverage.outputs.coverage_percentage }}';
            const statusEmoji = !testsFailed ? '✅' : '❌';

            const comment = `## ${statusEmoji} Test Coverage Report

            ### Test Results
            ${testsFailed ? '❌ Some tests failed' : '✅ All tests passed'}

            ### Coverage Summary
            **Coverage:** ${coverage}

            ### Detailed Results
            \`\`\`
            ${testResults.slice(-2000)} // Truncated for readability
            \`\`\`

            📊 Full coverage report available in artifacts

            ---
            *Test Coverage Report generated by PR Automation Workflow*`;

            github.rest.issues.createComment({
              issue_number: context.issue.number,
              owner: context.repo.owner,
              repo: context.repo.repo,
              body: comment
            });

  security-scan:
    name: security-scan
    runs-on: ubuntu-latest
    steps:
      - name: Checkout code
        uses: actions/checkout@v4

      - name: Setup Node.js
        uses: actions/setup-node@v4
        with:
          node-version: '18'
          cache: 'npm'

      - name: Install dependencies
        run: npm ci

      - name: Run dependency vulnerability scan
        id: audit
        run: |
          npm audit --json > audit-results.json 2>&1 || echo "audit_issues=true" >> $GITHUB_OUTPUT
          npm audit > audit-readable.txt 2>&1 || true
          cat audit-readable.txt

      - name: Scan for secrets in code changes
        id: secrets
        run: |
          # Simple secret pattern detection
          echo "Scanning for potential secrets in changed files..."
          git diff --name-only origin/main...HEAD | while read file; do
            if [ -f "$file" ]; then
              # Check for common secret patterns
              if grep -i -E "(password|secret|key|token|api_key)" "$file" > /dev/null 2>&1; then
                echo "⚠️  Potential secret found in: $file"
                grep -n -i -E "(password|secret|key|token|api_key)" "$file" || true
              fi
            fi
          done > secret-scan-results.txt 2>&1

          if [ -s secret-scan-results.txt ]; then
            echo "secrets_found=true" >> $GITHUB_OUTPUT
          else
            echo "✅ No potential secrets detected" > secret-scan-results.txt
          fi

          cat secret-scan-results.txt

      - name: Post Security Scan Report
        uses: actions/github-script@v7
        if: always()
        with:
          script: |
            const fs = require('fs');

            let auditResults = '';
            let secretResults = '';

            try {
              auditResults = fs.readFileSync('audit-readable.txt', 'utf8');
            } catch (e) {
              auditResults = 'Audit results could not be read';
            }

            try {
              secretResults = fs.readFileSync('secret-scan-results.txt', 'utf8');
            } catch (e) {
              secretResults = 'Secret scan results could not be read';
            }

            const auditIssues = '${{ steps.audit.outputs.audit_issues }}' === 'true';
            const secretsFound = '${{ steps.secrets.outputs.secrets_found }}' === 'true';

            let vulnerabilityCount = 0;
            try {
              const auditJson = JSON.parse(fs.readFileSync('audit-results.json', 'utf8'));
              vulnerabilityCount = auditJson.metadata?.vulnerabilities?.total || 0;
            } catch (e) {
              // Continue with default value
            }

            const hasIssues = auditIssues || secretsFound;
            const statusEmoji = !hasIssues ? '✅' : '⚠️';

            const comment = `## ${statusEmoji} Security Scan Report

            ### Dependencies Vulnerabilities
            **Total Vulnerabilities Found:** ${vulnerabilityCount}
            ${auditIssues ? '⚠️ Security issues detected in Dependencies' : '✅ No known vulnerabilities in Dependencies'}

            \`\`\`
            ${auditResults.slice(0, 1500)} // Truncated for readability
            \`\`\`

            ### Secret Detection Scan
            ${secretsFound ? '⚠️ Potential secrets detected' : '✅ No potential secrets detected'}

            \`\`\`
            ${secretResults}
            \`\`\`

            ${hasIssues ? '⚠️ **Action Required:** Please review and address the security issues above.' : '✅ **All Clear:** No security issues detected.'}

            ---
            *Security Scan Report generated by PR Automation Workflow*`;

            github.rest.issues.createComment({
              issue_number: context.issue.number,
              owner: context.repo.owner,
              repo: context.repo.repo,
              body: comment
            });

  build-validation:
    name: build-validation
    runs-on: ubuntu-latest
    steps:
      - name: Checkout code
        uses: actions/checkout@v4

      - name: Setup Node.js
        uses: actions/setup-node@v4
        with:
          node-version: '18'
          cache: 'npm'

      - name: Install dependencies
        run: npm ci

      - name: Build application
        id: build
        run: |
          npm run build > build-results.txt 2>&1 || echo "build_failed=true" >> $GITHUB_OUTPUT
          cat build-results.txt

      - name: Validate build artifacts
        id: validate
        run: |
          echo "Validating build artifacts..." > validation-results.txt

          # Check if common build outputs exist
          if [ -d "dist" ] || [ -d "build" ] || [ -f "index.js" ]; then
            echo "✅ Build artifacts found" >> validation-results.txt
          else
            echo "⚠️ Expected build artifacts not found" >> validation-results.txt
            echo "validation_issues=true" >> $GITHUB_OUTPUT
          fi

          # Simulate endpoint validation (basic checks)
          echo "🔍 Performing basic application validation..." >> validation-results.txt

          # Check if package.json has start script
          if grep -q '"start"' package.json; then
            echo "✅ Start script found in package.json" >> validation-results.txt
          else
            echo "⚠️ No start script found in package.json" >> validation-results.txt
          fi

          # Check for main entry point
          if [ -f "$(node -p "require('./package.json').main || 'index.js'")" ]; then
            echo "✅ Main entry point accessible" >> validation-results.txt
          else
            echo "⚠️ Main entry point not found" >> validation-results.txt
          fi

          cat validation-results.txt

      - name: Create deployment preview artifacts
        run: |
          mkdir -p deployment-preview
          cp -r . deployment-preview/ 2>/dev/null || true
          echo "Deployment preview created at $(date)" > deployment-preview/BUILD_INFO.txt
          echo "Commit: ${{ github.sha }}" >> deployment-preview/BUILD_INFO.txt
          echo "Branch: ${{ github.head_ref }}" >> deployment-preview/BUILD_INFO.txt

      - name: Upload deployment artifacts
        uses: actions/upload-artifact@v4
        with:
          name: deployment-preview
          path: deployment-preview/
          retention-days: 7

      - name: Post Build Validation Report
        uses: actions/github-script@v7
        if: always()
        with:
          script: |
            const fs = require('fs');

            let buildResults = '';
            let validationResults = '';

            try {
              buildResults = fs.readFileSync('build-results.txt', 'utf8');
            } catch (e) {
              buildResults = 'Build results could not be read';
            }

            try {
              validationResults = fs.readFileSync('validation-results.txt', 'utf8');
            } catch (e) {
              validationResults = 'Validation results could not be read';
            }

            const buildFailed = '${{ steps.build.outputs.build_failed }}' === 'true';
            const validationIssues = '${{ steps.validate.outputs.validation_issues }}' === 'true';

            const hasIssues = buildFailed || validationIssues;
            const statusEmoji = !hasIssues ? '✅' : '❌';

            const comment = `## ${statusEmoji} Build Validation Report

            ### Build Status
            ${buildFailed ? '❌ Build failed' : '✅ Build completed successfully'}

            \`\`\`
            ${buildResults}
            \`\`\`

            ### Validation Results
            ${validationIssues ? '⚠️ Validation issues detected' : '✅ All validations passed'}

            \`\`\`
            ${validationResults}
            \`\`\`

            ### Deployment Preview
            📦 Deployment preview artifacts have been created and uploaded

            ${hasIssues ? '❌ **Build Issues Detected:** Please review and fix the issues above.' : '✅ **Build Ready:** Application built and validated successfully.'}

            ---
            *Build Validation Report generated by PR Automation Workflow*`;

            github.rest.issues.createComment({
              issue_number: context.issue.number,
              owner: context.repo.owner,
              repo: context.repo.repo,
              body: comment
            });", "message": "Add comprehensive PR automation workflow with parallel jobs", "owner": "mcpmark-eval", "path": ".github/workflows/pr-automation.yml", "repo": "mcpmark-cicd"}
