{"sql": "
CREATE OR REPLACE FUNCTION check_set_parts_consistency()
RETURNS TRIGGER AS $$
DECLARE
    relevant_set_num TEXT;
    stored_num_parts INTEGER;
    actual_parts INTEGER;
BEGIN
    -- Identify the set_num based on which table fired the trigger
    IF TG_TABLE_NAME = 'lego_sets' OR TG_TABLE_NAME = 'lego_inventories' THEN
        relevant_set_num := NEW.set_num;
    ELSIF TG_TABLE_NAME = 'lego_inventory_parts' THEN
        -- Query lego_inventories to find the set_num for this inventory_id
        SELECT set_num INTO relevant_set_num
        FROM public.lego_inventories
        WHERE id = NEW.inventory_id;
    END IF;

    -- Get the stored num_parts from lego_sets
    SELECT num_parts INTO stored_num_parts
    FROM public.lego_sets
    WHERE set_num = relevant_set_num;

    -- Calculate the actual parts count for the latest inventory version
    SELECT COALESCE(SUM(ip.quantity), 0) INTO actual_parts
    FROM public.lego_inventories inv
    INNER JOIN (
        SELECT 
            set_num,
            MAX(version) AS max_version
        FROM 
            public.lego_inventories
        GROUP BY 
            set_num
    ) latest ON inv.set_num = latest.set_num AND inv.version = latest.max_version
    INNER JOIN public.lego_inventory_parts ip ON inv.id = ip.inventory_id
    WHERE 
        inv.set_num = relevant_set_num
        AND ip.is_spare = false;

    -- Raise an exception if the counts don't match
    IF stored_num_parts IS NOT NULL AND stored_num_parts <> actual_parts THEN
        RAISE EXCEPTION 'Inconsistent part count for set %: stored=%, actual=%', 
            relevant_set_num, stored_num_parts, actual_parts;
    END IF;

    RETURN NEW;
END;
$$ LANGUAGE plpgsql;
"}

