"}

{"query": "
-- Test 3: Verify inventories are filtered (only for Star Wars sets)
SET ROLE theme_analyst;
SELECT i.id, i.set_num, i.version, s.name as set_name, s.theme_id
FROM lego_inventories i
LEFT JOIN lego_sets s ON i.set_num = s.set_num
ORDER BY i.id
LIMIT 10;
"}

