{"query": "
-- Create monthly_sales_summary table
CREATE TABLE IF NOT EXISTS public.monthly_sales_summary (
    year_month VARCHAR(7) PRIMARY KEY,
    total_invoices INTEGER NOT NULL,
    total_revenue DECIMAL(10, 2) NOT NULL,
    total_tracks_sold INTEGER NOT NULL,
    average_invoice_value DECIMAL(10, 2) NOT NULL,
    unique_customers INTEGER NOT NULL
);

-- Create top_music_charts table
CREATE TABLE IF NOT EXISTS public.top_music_charts (
    chart_type VARCHAR(20) NOT NULL,
    rank_position INTEGER NOT NULL,
    item_id INTEGER NOT NULL,
    item_name VARCHAR(500) NOT NULL,
    total_revenue DECIMAL(10, 2) NOT NULL,
    PRIMARY KEY (chart_type, rank_position)
);

-- Create indexes for better query performance
CREATE INDEX IF NOT EXISTS idx_monthly_sales_year_month ON public.monthly_sales_summary(year_month);
CREATE INDEX IF NOT EXISTS idx_top_charts_type ON public.top_music_charts(chart_type);
CREATE INDEX IF NOT EXISTS idx_top_charts_rank ON public.top_music_charts(rank_position);
"}

{"query": "
-- Verify monthly sales summary (show first 10 months)
SELECT * FROM public.monthly_sales_summary 
ORDER BY year_month 
LIMIT 10;
"}

{"query": "
-- Verify top tracks chart
SELECT * FROM public.top_music_charts 
WHERE chart_type = 'top_tracks'
ORDER BY rank_position;
"}

