{"query": "
-- =====================================================
-- HELPER FUNCTIONS FOR RLS PERMISSION CHECKING
-- =====================================================

-- Function to check if a user is the owner of a channel
CREATE OR REPLACE FUNCTION public.is_channel_owner(p_channel_id UUID, p_user_id UUID)
RETURNS BOOLEAN AS $$
BEGIN
  RETURN EXISTS (
    SELECT 1 FROM public.channels 
    WHERE id = p_channel_id AND owner_id = p_user_id
  );
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to check if a user is a moderator of a channel
CREATE OR REPLACE FUNCTION public.is_channel_moderator(p_channel_id UUID, p_user_id UUID)
RETURNS BOOLEAN AS $$
BEGIN
  RETURN EXISTS (
    SELECT 1 FROM public.channel_moderators 
    WHERE channel_id = p_channel_id AND user_id = p_user_id
  );
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to check if a user can moderate a channel (owner OR moderator)
CREATE OR REPLACE FUNCTION public.can_moderate_channel(p_channel_id UUID, p_user_id UUID)
RETURNS BOOLEAN AS $$
BEGIN
  RETURN public.is_channel_owner(p_channel_id, p_user_id) OR 
         public.is_channel_moderator(p_channel_id, p_user_id);
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to get the current user ID from session context
CREATE OR REPLACE FUNCTION public.get_current_user_id()
RETURNS UUID AS $$
BEGIN
  RETURN current_setting('app.current_user_id', true)::UUID;
EXCEPTION
  WHEN OTHERS THEN
    RETURN NULL;
END;
$$ LANGUAGE plpgsql STABLE;

COMMENT ON FUNCTION public.is_channel_owner IS 'Check if a user is the owner of a channel';
COMMENT ON FUNCTION public.is_channel_moderator IS 'Check if a user is a moderator of a channel';
COMMENT ON FUNCTION public.can_moderate_channel IS 'Check if a user can moderate a channel (owner or moderator)';
COMMENT ON FUNCTION public.get_current_user_id IS 'Get current authenticated user ID from session context';
"}

