-- =====================================================
-- USERS TABLE RLS POLICIES
-- =====================================================

-- SELECT: Users can read all public user profiles or their own profile
CREATE POLICY "users_select_policy" ON public.users
  FOR SELECT
  USING (
    is_public = true OR 
    id = public.get_current_user_id()
  );

-- UPDATE: Users can only modify their own profile
CREATE POLICY "users_update_policy" ON public.users
  FOR UPDATE
  USING (id = public.get_current_user_id())
  WITH CHECK (id = public.get_current_user_id());

-- DELETE: Users can only delete their own account
CREATE POLICY "users_delete_policy" ON public.users
  FOR DELETE
  USING (id = public.get_current_user_id());

COMMENT ON POLICY "users_select_policy" ON public.users IS 'Users can read all public profiles or their own';
COMMENT ON POLICY "users_update_policy" ON public.users IS 'Users can only update their own profile';
COMMENT ON POLICY "users_delete_policy" ON public.users IS 'Users can only delete their own account';
"}

