-- =====================================================
-- CHANNELS TABLE RLS POLICIES
-- =====================================================

-- SELECT: Everyone can read public channel information
CREATE POLICY "channels_select_policy" ON public.channels
  FOR SELECT
  USING (is_public = true);

-- INSERT: Any authenticated user can create a channel (becomes owner)
CREATE POLICY "channels_insert_policy" ON public.channels
  FOR INSERT
  WITH CHECK (
    public.get_current_user_id() IS NOT NULL AND
    owner_id = public.get_current_user_id()
  );

-- UPDATE: Only channel owners can modify channel details
CREATE POLICY "channels_update_policy" ON public.channels
  FOR UPDATE
  USING (owner_id = public.get_current_user_id())
  WITH CHECK (owner_id = public.get_current_user_id());

-- DELETE: Only channel owners can delete channels
CREATE POLICY "channels_delete_policy" ON public.channels
  FOR DELETE
  USING (owner_id = public.get_current_user_id());

COMMENT ON POLICY "channels_select_policy" ON public.channels IS 'Everyone can read public channels';
COMMENT ON POLICY "channels_insert_policy" ON public.channels IS 'Authenticated users can create channels';
COMMENT ON POLICY "channels_update_policy" ON public.channels IS 'Only channel owners can update channels';
COMMENT ON POLICY "channels_delete_policy" ON public.channels IS 'Only channel owners can delete channels';
"}

