-- =====================================================
-- POSTS TABLE RLS POLICIES
-- =====================================================

-- SELECT: Users can read all posts in public channels
CREATE POLICY "posts_select_policy" ON public.posts
  FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM public.channels 
      WHERE channels.id = posts.channel_id 
      AND channels.is_public = true
    )
  );

-- INSERT: Authenticated users can create posts in any public channel
CREATE POLICY "posts_insert_policy" ON public.posts
  FOR INSERT
  WITH CHECK (
    public.get_current_user_id() IS NOT NULL AND
    author_id = public.get_current_user_id() AND
    EXISTS (
      SELECT 1 FROM public.channels 
      WHERE channels.id = posts.channel_id 
      AND channels.is_public = true
    )
  );

-- UPDATE: Post authors OR channel moderators OR channel owners can edit posts
CREATE POLICY "posts_update_policy" ON public.posts
  FOR UPDATE
  USING (
    author_id = public.get_current_user_id() OR
    public.can_moderate_channel(channel_id, public.get_current_user_id())
  )
  WITH CHECK (
    author_id = public.get_current_user_id() OR
    public.can_moderate_channel(channel_id, public.get_current_user_id())
  );

-- DELETE: Post authors OR channel moderators OR channel owners can delete posts
CREATE POLICY "posts_delete_policy" ON public.posts
  FOR DELETE
  USING (
    author_id = public.get_current_user_id() OR
    public.can_moderate_channel(channel_id, public.get_current_user_id())
  );

COMMENT ON POLICY "posts_select_policy" ON public.posts IS 'Users can read posts in public channels';
COMMENT ON POLICY "posts_insert_policy" ON public.posts IS 'Authenticated users can create posts in public channels';
COMMENT ON POLICY "posts_update_policy" ON public.posts IS 'Post authors or channel moderators can update posts';
COMMENT ON POLICY "posts_delete_policy" ON public.posts IS 'Post authors or channel moderators can delete posts';
"}

