-- =====================================================
-- COMMENTS TABLE RLS POLICIES
-- =====================================================

-- SELECT: Users can read comments on posts they can access
CREATE POLICY "comments_select_policy" ON public.comments
  FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM public.posts 
      JOIN public.channels ON channels.id = posts.channel_id
      WHERE posts.id = comments.post_id 
      AND channels.is_public = true
    )
  );

-- INSERT: Authenticated users can comment on posts they can see
CREATE POLICY "comments_insert_policy" ON public.comments
  FOR INSERT
  WITH CHECK (
    public.get_current_user_id() IS NOT NULL AND
    author_id = public.get_current_user_id() AND
    EXISTS (
      SELECT 1 FROM public.posts 
      JOIN public.channels ON channels.id = posts.channel_id
      WHERE posts.id = comments.post_id 
      AND channels.is_public = true
    )
  );

-- UPDATE: Comment authors OR post authors OR channel moderators OR channel owners can edit comments
CREATE POLICY "comments_update_policy" ON public.comments
  FOR UPDATE
  USING (
    author_id = public.get_current_user_id() OR
    EXISTS (
      SELECT 1 FROM public.posts 
      WHERE posts.id = comments.post_id 
      AND (
        posts.author_id = public.get_current_user_id() OR
        public.can_moderate_channel(posts.channel_id, public.get_current_user_id())
      )
    )
  )
  WITH CHECK (
    author_id = public.get_current_user_id() OR
    EXISTS (
      SELECT 1 FROM public.posts 
      WHERE posts.id = comments.post_id 
      AND (
        posts.author_id = public.get_current_user_id() OR
        public.can_moderate_channel(posts.channel_id, public.get_current_user_id())
      )
    )
  );

-- DELETE: Comment authors OR post authors OR channel moderators OR channel owners can delete comments
CREATE POLICY "comments_delete_policy" ON public.comments
  FOR DELETE
  USING (
    author_id = public.get_current_user_id() OR
    EXISTS (
      SELECT 1 FROM public.posts 
      WHERE posts.id = comments.post_id 
      AND (
        posts.author_id = public.get_current_user_id() OR
        public.can_moderate_channel(posts.channel_id, public.get_current_user_id())
      )
    )
  );

COMMENT ON POLICY "comments_select_policy" ON public.comments IS 'Users can read comments on accessible posts';
COMMENT ON POLICY "comments_insert_policy" ON public.comments IS 'Authenticated users can comment on accessible posts';
COMMENT ON POLICY "comments_update_policy" ON public.comments IS 'Comment authors, post authors, or moderators can update comments';
COMMENT ON POLICY "comments_delete_policy" ON public.comments IS 'Comment authors, post authors, or moderators can delete comments';
"}

