-- =====================================================
-- CHANNEL_MODERATORS TABLE RLS POLICIES
-- =====================================================

-- SELECT: Users can see moderator lists for public channels
CREATE POLICY "channel_moderators_select_policy" ON public.channel_moderators
  FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM public.channels 
      WHERE channels.id = channel_moderators.channel_id 
      AND channels.is_public = true
    )
  );

-- INSERT: Only channel owners can add moderators
CREATE POLICY "channel_moderators_insert_policy" ON public.channel_moderators
  FOR INSERT
  WITH CHECK (
    public.is_channel_owner(channel_id, public.get_current_user_id())
  );

-- DELETE: Channel owners can remove moderators; moderators can remove themselves
CREATE POLICY "channel_moderators_delete_policy" ON public.channel_moderators
  FOR DELETE
  USING (
    public.is_channel_owner(channel_id, public.get_current_user_id()) OR
    user_id = public.get_current_user_id()
  );

COMMENT ON POLICY "channel_moderators_select_policy" ON public.channel_moderators IS 'Users can see moderators for public channels';
COMMENT ON POLICY "channel_moderators_insert_policy" ON public.channel_moderators IS 'Only channel owners can add moderators';
COMMENT ON POLICY "channel_moderators_delete_policy" ON public.channel_moderators IS 'Channel owners can remove moderators; moderators can remove themselves';
"}

