-- =====================================================
-- RLS IMPLEMENTATION SUMMARY AND DOCUMENTATION
-- =====================================================

-- Create a summary view to document all policies
COMMENT ON TABLE public.users IS 'User profiles with RLS policies: SELECT (public profiles or own), UPDATE (own only), DELETE (own only)';
COMMENT ON TABLE public.channels IS 'Channels with RLS policies: SELECT (public), INSERT (authenticated), UPDATE/DELETE (owner only)';
COMMENT ON TABLE public.posts IS 'Posts with RLS policies: SELECT (public channels), INSERT (authenticated in public channels), UPDATE/DELETE (author or channel moderators)';
COMMENT ON TABLE public.comments IS 'Comments with RLS policies: SELECT (on accessible posts), INSERT (authenticated on accessible posts), UPDATE/DELETE (author, post author, or channel moderators)';
COMMENT ON TABLE public.channel_moderators IS 'Channel moderators with RLS policies: SELECT (public channels), INSERT (channel owner only), DELETE (channel owner or self-removal)';

-- Create a helper view to check RLS status (read-only)
CREATE OR REPLACE VIEW public.rls_implementation_status AS
SELECT 
  'Row Level Security Implementation Complete' AS status,
  5 AS tables_with_rls_enabled,
  4 AS helper_functions_created,
  jsonb_build_object(
    'users', '3 policies (SELECT, UPDATE, DELETE)',
    'channels', '4 policies (SELECT, INSERT, UPDATE, DELETE)',
    'posts', '4 policies (SELECT, INSERT, UPDATE, DELETE)',
    'comments', '4 policies (SELECT, INSERT, UPDATE, DELETE)',
    'channel_moderators', '3 policies (SELECT, INSERT, DELETE)'
  ) AS policy_summary,
  jsonb_build_object(
    'is_channel_owner', 'Check if user owns a channel',
    'is_channel_moderator', 'Check if user is a moderator',
    'can_moderate_channel', 'Check if user can moderate (owner OR moderator)',
    'get_current_user_id', 'Get current authenticated user ID from session'
  ) AS helper_functions;

COMMENT ON VIEW public.rls_implementation_status IS 'Summary view of RLS implementation for the social media platform';
"}
