{"query": "
SELECT column_name, data_type, character_maximum_length, is_nullable
FROM information_schema.columns
WHERE table_schema = 'employees' AND table_name = 'employee'
ORDER BY ordinal_position;
"}

{"query": "
CREATE TABLE employees.employee_projects (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(100) NOT NULL,
    start_date DATE NOT NULL,
    end_date DATE,
    budget DECIMAL(10,2),
    status VARCHAR(20) DEFAULT 'active'
);
"}

{"query": "
CREATE TABLE employees.project_assignments (
    assignment_id SERIAL PRIMARY KEY,
    employee_id BIGINT NOT NULL,
    project_id INTEGER NOT NULL,
    role VARCHAR(50) NOT NULL,
    allocation_percentage INTEGER CHECK (allocation_percentage BETWEEN 1 AND 100),
    assigned_date DATE NOT NULL
);
"}

{"query": "
CREATE TABLE employees.project_milestones (
    milestone_id SERIAL PRIMARY KEY,
    project_id INTEGER NOT NULL,
    milestone_name VARCHAR(100) NOT NULL,
    due_date DATE NOT NULL,
    completed BOOLEAN DEFAULT false
);
"}

{"query": "
ALTER TABLE employees.project_assignments
ADD CONSTRAINT fk_project_assignments_employee
FOREIGN KEY (employee_id) REFERENCES employees.employee(id);
"}

{"query": "
ALTER TABLE employees.project_assignments
ADD CONSTRAINT fk_project_assignments_project
FOREIGN KEY (project_id) REFERENCES employees.employee_projects(project_id);
"}

{"query": "
ALTER TABLE employees.project_milestones
ADD CONSTRAINT fk_project_milestones_project
FOREIGN KEY (project_id) REFERENCES employees.employee_projects(project_id);
"}

{"query": "
-- Update Project 3 status to 'completed'
UPDATE employees.employee_projects 
SET status = 'completed' 
WHERE project_name = 'HR Analytics Dashboard';
"}

{"query": "
-- Increase budget by 15% for all projects with status 'active'
UPDATE employees.employee_projects 
SET budget = budget * 1.15 
WHERE status = 'active';
"}

{"query": "
-- Mark the milestone 'Data Collection' as completed
UPDATE employees.project_milestones 
SET completed = true 
WHERE milestone_name = 'Data Collection';
"}

{"query": "
-- Add priority column with check constraint
ALTER TABLE employees.employee_projects 
ADD COLUMN priority VARCHAR(10) 
CHECK (priority IN ('low', 'medium', 'high'));
"}

{"query": "
-- Update priority: 'high' for 'Database Modernization', 'medium' for others
UPDATE employees.employee_projects 
SET priority = 'high' 
WHERE project_name = 'Database Modernization';
"}

{"query": "
-- Update priority to 'medium' for all other projects
UPDATE employees.employee_projects 
SET priority = 'medium' 
WHERE project_name != 'Database Modernization';
"}

