-- ============================================
-- HELPER FUNCTIONS FOR PERMISSION CHECKS
-- ============================================

-- Function to check if a user is the owner of a channel
CREATE OR REPLACE FUNCTION public.is_channel_owner(p_channel_id UUID, p_user_id UUID)
RETURNS BOOLEAN AS $$
BEGIN
  RETURN EXISTS (
    SELECT 1 FROM public.channels
    WHERE id = p_channel_id AND owner_id = p_user_id
  );
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to check if a user is a moderator of a channel
CREATE OR REPLACE FUNCTION public.is_channel_moderator(p_channel_id UUID, p_user_id UUID)
RETURNS BOOLEAN AS $$
BEGIN
  RETURN EXISTS (
    SELECT 1 FROM public.channel_moderators
    WHERE channel_id = p_channel_id AND user_id = p_user_id
  );
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to check if a user can moderate a channel (owner OR moderator)
CREATE OR REPLACE FUNCTION public.can_moderate_channel(p_channel_id UUID, p_user_id UUID)
RETURNS BOOLEAN AS $$
BEGIN
  RETURN public.is_channel_owner(p_channel_id, p_user_id) 
      OR public.is_channel_moderator(p_channel_id, p_user_id);
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to get channel_id from post_id
CREATE OR REPLACE FUNCTION public.get_channel_id_from_post(p_post_id UUID)
RETURNS UUID AS $$
BEGIN
  RETURN (SELECT channel_id FROM public.posts WHERE id = p_post_id);
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

-- Function to get post author_id
CREATE OR REPLACE FUNCTION public.get_post_author(p_post_id UUID)
RETURNS UUID AS $$
BEGIN
  RETURN (SELECT author_id FROM public.posts WHERE id = p_post_id);
END;
$$ LANGUAGE plpgsql STABLE SECURITY DEFINER;

COMMENT ON FUNCTION public.is_channel_owner IS 'Check if user owns a channel';
COMMENT ON FUNCTION public.is_channel_moderator IS 'Check if user is a moderator of a channel';
COMMENT ON FUNCTION public.can_moderate_channel IS 'Check if user can moderate (owner or moderator)';
COMMENT ON FUNCTION public.get_channel_id_from_post IS 'Get channel_id for a given post';
COMMENT ON FUNCTION public.get_post_author IS 'Get author_id for a given post';
"}

