-- ============================================
-- CHANNEL MODERATORS TABLE RLS POLICIES
-- ============================================

-- SELECT: Users can see moderator lists for channels
CREATE POLICY moderators_select_all ON public.channel_moderators
  FOR SELECT
  USING (
    EXISTS (
      SELECT 1 FROM public.channels c
      WHERE c.id = channel_moderators.channel_id
      AND (
        c.is_public = true
        OR c.owner_id::text = current_setting('app.current_user_id', true)
        OR public.is_channel_moderator(c.id, current_setting('app.current_user_id', true)::uuid)
      )
    )
  );

-- INSERT: Only channel owners can add moderators
CREATE POLICY moderators_insert_owner ON public.channel_moderators
  FOR INSERT
  WITH CHECK (
    public.is_channel_owner(channel_id, current_setting('app.current_user_id', true)::uuid)
  );

-- DELETE: Channel owners can remove moderators; moderators can remove themselves
CREATE POLICY moderators_delete_owner_or_self ON public.channel_moderators
  FOR DELETE
  USING (
    public.is_channel_owner(channel_id, current_setting('app.current_user_id', true)::uuid)
    OR user_id::text = current_setting('app.current_user_id', true)
  );

-- UPDATE: Not typically needed for junction tables, but adding for completeness
-- Only owners can update moderator records
CREATE POLICY moderators_update_owner ON public.channel_moderators
  FOR UPDATE
  USING (
    public.is_channel_owner(channel_id, current_setting('app.current_user_id', true)::uuid)
  )
  WITH CHECK (
    public.is_channel_owner(channel_id, current_setting('app.current_user_id', true)::uuid)
  );
"}

