-- ============================================
-- PERFORMANCE INDEXES FOR RLS POLICIES
-- ============================================

-- Index for efficient channel access checks in posts
CREATE INDEX IF NOT EXISTS idx_posts_channel_author ON public.posts(channel_id, author_id);

-- Index for efficient channel access checks in comments via posts
CREATE INDEX IF NOT EXISTS idx_comments_post_author ON public.comments(post_id, author_id);

-- Composite index for channel moderator lookups
-- (already exists as idx_channel_moderators_channel_user, but ensuring it's optimal)
CREATE INDEX IF NOT EXISTS idx_moderators_lookup ON public.channel_moderators(channel_id, user_id)
WHERE channel_id IS NOT NULL AND user_id IS NOT NULL;

-- Index for efficient owner lookups on channels
-- (already exists as idx_channels_owner_id)

-- Index for posts with channel and timestamp for feed queries
CREATE INDEX IF NOT EXISTS idx_posts_channel_created ON public.posts(channel_id, created_at DESC);

-- Index for comments with post and timestamp
CREATE INDEX IF NOT EXISTS idx_comments_post_created ON public.comments(post_id, created_at DESC);
