{"query": "
-- Step 2: Create indexes to optimize the query

-- Index on participants_events for filtering and grouping
CREATE INDEX IF NOT EXISTS idx_participants_events_participant_id 
ON participants_events(participant_id);

-- Index on participants_events for join with events
CREATE INDEX IF NOT EXISTS idx_participants_events_event_id 
ON participants_events(event_id);

-- Composite index on stats for the correlated subqueries
-- This will speed up lookups by stat_holder_id and stat_holder_type
CREATE INDEX IF NOT EXISTS idx_stats_holder 
ON stats(stat_holder_id, stat_holder_type);

-- Additional index for the DISTINCT stat_repository_type query
CREATE INDEX IF NOT EXISTS idx_stats_holder_repository 
ON stats(stat_holder_id, stat_holder_type, stat_repository_type);

-- Index on events for the join and start_date_time lookup
CREATE INDEX IF NOT EXISTS idx_events_id 
ON events(id);

-- Index on events.start_date_time for MAX aggregation
CREATE INDEX IF NOT EXISTS idx_events_start_date_time 
ON events(start_date_time);
"}
