{"sql": "
CREATE TABLE player_injury_status (
    status_id SERIAL PRIMARY KEY,
    person_id INTEGER UNIQUE NOT NULL,
    injury_count INTEGER DEFAULT 0,
    last_injury_date DATE,
    current_status VARCHAR CHECK (current_status IN ('healthy', 'injured', 'recovering'))
);
"}

{"sql": "
CREATE TABLE team_performance_summary (
    summary_id SERIAL PRIMARY KEY,
    metric_name VARCHAR UNIQUE,
    metric_value DECIMAL
);
"}

{"sql": "
SELECT metric_name, metric_value
FROM team_performance_summary
ORDER BY summary_id;
"}
