{"name": "create_monthly_sales_summary_table", "query": "
-- Create monthly sales summary table
CREATE TABLE IF NOT EXISTS public.monthly_sales_summary (
  year_month VARCHAR(7) PRIMARY KEY,
  total_invoices INTEGER NOT NULL,
  total_revenue DECIMAL(10, 2) NOT NULL,
  total_tracks_sold INTEGER NOT NULL,
  average_invoice_value DECIMAL(10, 2) NOT NULL,
  unique_customers INTEGER NOT NULL
);

COMMENT ON TABLE public.monthly_sales_summary IS 'Monthly sales dashboard summary for management reporting';
COMMENT ON COLUMN public.monthly_sales_summary.year_month IS 'Month in YYYY-MM format';
COMMENT ON COLUMN public.monthly_sales_summary.total_invoices IS 'Number of invoices in the month';
COMMENT ON COLUMN public.monthly_sales_summary.total_revenue IS 'Total revenue generated in the month';
COMMENT ON COLUMN public.monthly_sales_summary.total_tracks_sold IS 'Total quantity of tracks sold';
COMMENT ON COLUMN public.monthly_sales_summary.average_invoice_value IS 'Average invoice amount for the month';
COMMENT ON COLUMN public.monthly_sales_summary.unique_customers IS 'Number of distinct customers who made purchases';
"}

{"name": "create_top_music_charts_table", "query": "
-- Create top music charts table
CREATE TABLE IF NOT EXISTS public.top_music_charts (
  chart_type VARCHAR(20) NOT NULL,
  rank_position INTEGER NOT NULL,
  item_id INTEGER NOT NULL,
  item_name VARCHAR(255) NOT NULL,
  total_revenue DECIMAL(10, 2) NOT NULL,
  PRIMARY KEY (chart_type, rank_position)
);

-- Create index for querying by chart type
CREATE INDEX IF NOT EXISTS idx_top_music_charts_chart_type ON public.top_music_charts(chart_type);

COMMENT ON TABLE public.top_music_charts IS 'Top 10 charts for tracks, albums, and artists';
COMMENT ON COLUMN public.top_music_charts.chart_type IS 'Type of chart: top_tracks, top_albums, or top_artists';
COMMENT ON COLUMN public.top_music_charts.rank_position IS 'Ranking position from 1 to 10';
COMMENT ON COLUMN public.top_music_charts.item_id IS 'ID of the track, album, or artist';
COMMENT ON COLUMN public.top_music_charts.item_name IS 'Name of the track, album, or artist';
COMMENT ON COLUMN public.top_music_charts.total_revenue IS 'Total revenue or quantity sold';
"}
